import React from "react";
import { useState } from "react";
import { useEffect } from "react";
import { useRef } from "react";
import "../Styles/AddItem.scss";

function AddItem({ itemsList, setItemsList, editIndex, setEditIndex }) {
  const [item, setItem] = useState("");

  const eventReference = useRef(null);

  useEffect(() => {
    editIndex > -1 &&
      (eventReference.current.target.value = itemsList[editIndex].itemValue);
  }, [editIndex]);

  function itemChangeHandler(event) {
    eventReference.current = event;
    setItem(event.target.value);
  }

  function addItemHandler() {
    eventReference.current.target.value = "";
    setItem("");
    item.length > 0 &&
      setItemsList((itemsList) => [
        ...itemsList,
        { itemValue: item, isStrikethrough: false },
      ]);
  }

  function editItemHandler() {
    const updatedItemsList = [...itemsList];
    updatedItemsList.splice(editIndex, 1, {
      itemValue: eventReference.current.target.value,
      isStrikeThrough: false,
    });
    eventReference.current.target.value = "";
    setItemsList(updatedItemsList);
    setItem("");
    setEditIndex(-1);
  }

  function deleteAllHandler() {
    let updatedItemsList = [...itemsList];
    updatedItemsList = updatedItemsList.filter(
      (item) => item.isStrikeThrough === false
    );
    setItemsList(updatedItemsList);
  }

  return (
    <div className="add-item-input">
      <input
        placeholder="What would you like to add?"
        onChange={itemChangeHandler}
      />
      {itemsList.some((item) => item.isStrikeThrough === true) ? (
        <button className="edit-item-button" onClick={deleteAllHandler}>
          DeleteAll
        </button>
      ) : editIndex > -1 ? (
        <button className="edit-item-button" onClick={editItemHandler}>
          edit
        </button>
      ) : (
        <button className="add-item-button" onClick={addItemHandler}>
          Add
        </button>
      )}
    </div>
  );
}

export { AddItem };
