import React from "react";
import { useState } from "react";
import { useEffect } from "react";
import { useRef } from "react";
import "../Styles/AddItem.scss";

function AddItem({ itemsList, setItemsList, editIndex, setEditIndex }) {
  const [item, setItem] = useState("");

  const eventReference = useRef(null);

  useEffect(() => {
    editIndex > -1 &&
      (eventReference.current.target.value = itemsList[editIndex]);

    document.getElementsByClassName("edit-item-button").style.display =
      "inline";
  }, [editIndex]);

  function itemChangeHandler(event) {
    eventReference.current = event;
    setItem(event.target.value);
  }

  function addItemHandler() {
    eventReference.current.target.value = "";
    setItem("");
    item.length > 0 && setItemsList((itemsList) => [...itemsList, item]);
  }

  function editItemHandler() {
    const updatedItemsList = [...itemsList];
    updatedItemsList.splice(editIndex, 1, eventReference.current.target.value);
    setItemsList(updatedItemsList);
  }

  return (
    <div className="add-item-input">
      <input
        placeholder="What would you like to add?"
        onChange={itemChangeHandler}
      />
      <button className="add-item-button" onClick={addItemHandler}>
        Add
      </button>

      <button className="edit-item-button" onClick={editItemHandler}>
        edit
      </button>
    </div>
  );
}

export { AddItem };
